    padding-top  : 13px;
    right        : 15px;
    position     : fixed;
    z-index      : 9;
    margin-top   : 5px;
    z-index      : 99;


    @media screen and (min-width: $mobile-break) {
        display: none;
    }

}

.nav-wrapper {
    @media screen and (max-width: $mobile-break) {
        padding: 0 15px;
        display: none;
    }
}

.openNav {
    display: block !important;
}

